/* ----- generate counter ----- */
body {
  counter-reset: head;
}

h3.r:before {
  counter-increment: head;
  content: "λ" counter(head);

  margin-right: 5px;
  padding: 0px 4px;
  color: #666;
  border: 1px solid #666;
  -moz-border-radius: 4px;
  text-decoration: none !important;
}

/* ----- for jAutoPagerize ----- */
#autopagerize_icon + div { 
  z-index: 9999 !important;
}

relative-time:after,
time-ago:after {
    content: " " attr(title);
    opacity: .5;
}
.age time-ago {
    margin-right: -3.5em;
}

/*轮播图*/
html {
zoom: 100%;
}

main .carousel {
    width: 760px;
    height: 206px;
    background: #fff;
    margin: 0 auto 8px;
    display: none;
    overflow: hidden;
}

/*右边广告位*/
.right_top {
    height: 206px;
    display: none;
}

