    border: 1px solid #3385ff !important;
    border-radius: 5px !important;
  }

.wrapper_l .s_form {
  width: 760px !important;
  margin: 0 auto !important;
  transform: translatex(-40px);
}
.s_tab,
.nums,
.search_tool_conter,
#rs,
.to_tieba,
.to_zhidao_bottom {
  margin: 0 auto !important;
}
.headBlock {
  margin-left: 0;
}
.headBlock > div {
  margin: auto;
}
.head_nums_cont_inner > div {
  width: 600px;
}
#page,
#s_tab {
  padding-left: 0px !important;
  text-align: center !important;
}
#container {
  width: 100%;
}
#content_right {
  display: none;
}
#content_left {
  padding: 0;
  width: 96%;
  padding-left: 2%;
  display: block;
  counter-reset: results;
  column-gap: 0;
  -moz-column-gap: 0;
  -webkit-column-gap: 0;
}
.rrecom-container,
.xpath-log:not(.c-container) {
  transform: scale(0);
}
@media (min-width: 1201px) and (max-width: 1700px) {
  #content_left {
    column-count: 2;
    -moz-column-count: 2;
    -webkit-column-count: 2;
  }
}
@media (min-width: 1701px) and (max-width: 2200px) {
  #content_left {
    column-count: 3;
    -moz-column-count: 3;
    -webkit-column-count: 3;
  }
}
@media (min-width: 2201px) {
  #content_left {
    column-count: 4;
    -moz-column-count: 4;
    -webkit-column-count: 4;
  }
}
#content_left > div:not([class^="result"]) {
  position: absolute;
  margin-top: -1em;
  transform: scale(0);
}
#content_left > div[class^="result"] {
  display: inline-block;
  width: 91%;
  padding: 18px 15px;
  margin: 1em;
  word-break: normal;
  word-wrap: normal;
  position: relative;
  z-index: 1;
  box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
  border-radius: 2px;
  background-color: white;
  overflow: hidden;
}
#content_left > div[class^="result"]:before {
  counter-increment: results;
  content: counter(results);
  z-index: -10;
  color: #eee;
  font-size: 4.5em;
  position: absolute;
  text-align: right;
  width: 96%;
  line-height: 1em;
  top: 6px;
}
#content_left > div[class^="result"] .sitelink {
  word-wrap: break-word;
}
#content_left .result-op > div {
  width: 540px;
}
.c-border {
  border: 0;
  box-shadow: none;
}
.to_tieba {
  display: none;
}
.hint_common_restop {
  display: none;
}
#rs {
  width: 100%;
  text-align: center;
  padding: 0;
  margin: 0;
  margin-top: 10px;
}
#rs .tt,
#rs table,
#rs tr,
#rs td,
#rs th {
  display: inline;
  padding: 0 5px;
}
#page {
  margin: 15px 0 !important;
  text-align: centre !important;
  padding: 0 !important;
}
#page .fk {
  display: none !important;
}
#page > a,
#page > strong {
  height: auto !important;
}
#foot {
  margin-top: 35px !important;
  text-align: center !important;
}
#foot #help {
  float: none !important;
  padding: 0 !important;
}


