.t:hover>a{text-shadow: 1px 1px 20px #aaa;}
.t{
            position: relative;    
        }
        .t:before{
            content: "";
            position: absolute;
            left: 0%;
            bottom: -2px;
            width: 0;
            height: 2px;
            background-color: #3385ff;
            transition: all .3s;
        }
        .t:hover:before{
            width: 91%;
            left: 0;
            right: 0;
        }

