	width: 640px;
	margin-top: -300px;
}
#search-input {
	width: calc(100% - 55px);
}
#www-wikipedia-org #searchInput {
	width: calc(100% + 55px);
	padding-left: 13px;
	border: 0;
	border-radius: 3px;
	box-shadow: 0 1px 3px 0 rgba(0,0,0,0.15);
	transition: 0.3s;
}
#www-wikipedia-org #searchInput:focus {
	box-shadow: 0 1px 5px 0 rgba(0,0,0,0.3);
}
#search-input .styled-select {
	right: 0;
}
